    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String url = "jdbc:mysql://localhost:3306/abc";
        String uname = "root";
        String pass = "31101996";
        try{
        Class.forName("com.mysql.jdbc.Driver");
            Connection con = DriverManager.getConnection(url,uname,pass);
            
            
            String username = "";
            String name = "";
            //String password = "";
            //String password2 = "";
            name = jTextField1.getText();
            username = jTextField2.getText();
            String password = new String(jPasswordField3.getPassword());
            String password2 = new String(jPasswordField2.getPassword());
            if (password.equals(password2))
            {
            if(username.equals("") || password.equals("") || name.equals(""))
            {
                JOptionPane.showMessageDialog(null,"Empty Credentials");
            }
            else
            {
                String iquery1 = "insert into signup values(?,?,?)";
                String iquery2 = "insert into login values(?,?)";
                PreparedStatement st1 = con.prepareStatement(iquery1);
                PreparedStatement st2 = con.prepareStatement(iquery2);
                st1.setString(1,name);
                st1.setString(2,username);
                st1.setString(3, password);
                st1.executeUpdate();
                st1.close();
                
                st2.setString(1,username);
                st2.setString(2, password);
                st2.executeUpdate();
                st2.close();
                
                
                con.close();
            }
            }
            else
            {
                JOptionPane.showMessageDialog(null,"Password Doesn't Match. TRY AGAIN!!");
            }
        }catch(Exception e)
        {
            System.out.println(e);
        }
        
        Login login = new Login();
        setVisible(false);
        login.setVisible(true);
    }//GEN-LAST:event_jButton1ActionPerformed

